         <Link to={`/detail/${item.id}`}>ÏÉÅÏÑ∏Î≥¥Í∏∞</Link>
        <h2>{item.title}</h2>
        <div>{item.contents}</div> 
        </div>
        <ToDoListButton>
        <DeleteButton onClick={() => RemoveButton(item.id)}
        >ÏÇ≠Ï†úÌïòÍ∏∞</DeleteButton>
        <CancelB onClick={() => CancelButton(item.id)}
        >Ï∑®ÏÜå</CancelB>
        </ToDoListButton>
        </ToDoListBox>
      ))}
      </ToDoListLine>
    </>
    )
}

export default DoneToDos
