import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { deleteTodo, togleTodo } from '../redux/modules/todos'
import styled from 'styled-components'
import { Link } from 'react-router-dom'


function DoneToDos() {
    const todos = useSelector((state) => {
        return state.todos.todos
    })

    const dispatch = useDispatch()

    const DeleteBtn = (id) => {
        const newtodo = todos.filter(item => item.id !== id)
        dispatch(deleteTodo(newtodo))
    }

    const TogleBtn = (id) => {
        const newtodo = todos.map(item =>
            item.id === id ? { ...item, inSuccess : !item.inSuccess} : item)
            dispatch(togleTodo(newtodo));
    }

    const doneTodo = todos.filter(item => item.inSuccess)

    return (
        <>
    <WorkingName>Done üê∂</WorkingName>
        {doneTodo.map((item) => (
            <ToDoListBox key={item.id} >
            <div>
                <Link to={`/detail/${item.id}`}>ÏÉÅÏÑ∏Î≥¥Í∏∞</Link>
                <h2>{item.name}</h2>
                <div>{item.content}</div> 
            </div>
        <DeleteButton onClick={() => DeleteBtn(item.id)}
        >ÏÇ≠Ï†úÌïòÍ∏∞</DeleteButton>
        <CancelB onClick={() => TogleBtn(item.id)}
        >Ï∑®ÏÜå</CancelB>
        </ToDoListBox>
    ))}
    </>
    )
}

export default DoneToDos

const WorkingName = styled.div`
    padding: 20px 0px 20px 0px;
    font-size: 30px;
    font-weight: 700;
`;

const ToDoListBox = styled.div`
    border: 4px solid #d35e00;
    border-radius: 12px;
    padding: 12px 24px 24px;
    width: 270px;
`;

const ToDoListButton = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 25px;
`;

const DeleteButton = styled.button`
    background-color: #a94b00;
    height: 40px;
    width: 130px;
    border: none;
    border-radius: 12px;
    font-size: 15px;
    font-weight: 600;
    color: #ffffff;
`;

const CancelB = styled.button`
    background-color: #ffc87a;
    height: 40px;
    width: 130px;
    border: none;
    border-radius: 12px;
    font-size: 15px;
    font-weight: 600;
    color: #ffffff;
`;
