        <ToDoListBox key={item.id} >
        <div>
        <Link to={`/detail/${item.id}`}>ÏÉÅÏÑ∏Î≥¥Í∏∞</Link>
        <h2>{item.title}</h2>
        <div>{item.contents}</div> 
        </div>
        <ToDoListButton>
        <DeleteButton onClick={() => DeleteBtn(item.id)}
        >ÏÇ≠Ï†úÌïòÍ∏∞</DeleteButton>
        <CompleteButton onClick={() => ActionButton(item.id)}
        >ÏôÑÎ£åÌïòÍ∏∞</CompleteButton>
        </ToDoListButton>
        </ToDoListBox>
    ))}
        </ToDoListLine>
    </>
    )
}

export default WorkingToDos

const Font = styled.div`
    padding: 20px 0px 20px 0px;
    font-size: 30px;
    font-weight: 700;
`;

const ToDoListLine = styled.div`
    display: flex;
    flex-wrap: wrap;
    gap: 12px;
`;

const ToDoListBox = styled.div`
    border: 4px solid rgb(250, 233, 149);
    border-radius: 12px;
    padding: 12px 24px 24px;
    width: 270px;
`;

const ToDoListButton = styled.div`
    display: flex;
    gap: 10px;
    margin-top: 25px;
`;

const DeleteButton = styled.button`
    background-color: #97c563;
    height: 40px;
    width: 130px;
    border: none;
    border-radius: 12px;
    font-size: 15px;
    font-weight: 600;
    color: #ffffff;
`;

const CompleteButton = styled.button`
    background-color: #ffc87a;
    height: 40px;
    width: 130px;
    border: none;
    border-radius: 12px;
    font-size: 15px;
    font-weight: 600;
    color: #ffffff;
`;
